import {React} from "react";
import './index.css';
import HobbiesList from './components/HobbiesList.js';
import WelcomeMessage from "./components/wellcome-message.js";
import Button from './components/Button.js';
import ProfileCard from './co';



function App(){
  const handleClick = () => {
    alert('Button clicked!');
  };
  return (
    <>
    <WelcomeMessage/>
    <hr/>
    <HobbiesList/>

    <div className="p-6">
            <h1 className="text-xl font-bold mb-4"> Question3:  reusable button component with customizable text and color props.</h1>
            <Button text="Click Me" color="green" btnMargin = "20px" onClick={handleClick} />
            <Button text="Submit" color="red" btnMargin ="20px" onClick={handleClick} />
            <Button text="Default" onClick={handleClick} />

            {/* -------- profile card */}
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
            <ProfileCard 
                name="John Doe" 
                age={30} 
                email="johndoe@example.com" 
            />
        </div>
        </div>
    </>
  )
}
export default App;
