import Button from './components/Button.js';
import ProfileCard from './components/ProfileCard.js';



function App(){
  const handleClick = () => {
    alert('Button clicked!');
  };
  return (
    <>
    <WelcomeMessage/>
    <hr/>
    <HobbiesList/>

    <div className="p-6">
            <h1 className="text-xl font-bold mb-4"> Question3:  reusable button component with customizable text and color props.</h1>
            <Button text="Click Me" color="green" btnMargin = "20px" onClick={handleClick} />
            <Button text="Submit" color="red" btnMargin ="20px" onClick={handleClick} />
            <Button text="Default" onClick={handleClick} />

            {/* -------- profile card */}
            <div className="bg-gray-100 p-4">
            <h1 className="text-lg font-bold">4. Design a profile card component that accepts name, age, and email as props.</h1>
            <br/>
            <ProfileCard 
                name="rukundo wilson" 
                age={21} 
                email="rukundo@ewilson.com" minHeight ="250px"
                widthCard = "250px"
            />
        </div>
