import React, { useState, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import "./index.css";

// Question 1 Components
import HobbiesList from "./components/question1/HobbiesList.js";
import WelcomeMessage from "./components/question1/welcome-message.js";
import Button from "./components/question1/Button.js";
import ProfileCard from "./components/question1/ProfileCard.js";
import ItemsList from "./components/question1/ListItemComponent.js";
import DateDisplay from "./components/question1/DateDisplayComponent.js";

// Question 2 Components
import ToggleButton from "./components/question2/ToggleOnOff.js";
import Counter from "./components/question2/Counter.js";
import HoverBackground from "./components/question2/HoverBackgroundComponent.js";
import Form from "./components/question2/LogFormData.js";
import DropdownMenu from "./components/question2/DropDown.js";

// Question 3 Components
import LoginForm from "./components/question3/LoginFormComponent.js";
import ControlledForm from "./components/question3/ControlledFormComponent.js";
import ValidatedForm from "./components/question3/Email_password_validator.js";
import MultiStepForm from "./components/question3/MultiStepFormComponent.js";
import CheckboxForm from "./components/question3/CheckboxFormComponent.js";

// Question 4 Components
import NavigationBar from "./components/question4/NavigationBar.js";
import About from "./components/question4/About.js";
import Contact from "./components/question4/Contact.js";
import Home from "./components/question4/Home.js";
import ProductDetails from "./components/question4/ProductDetailsComponent.js";
import NotFound from "./components/question4/NotFound";

// Question 5 Components
import Parent from "./components/question5/Parent";
import CounterMem from "./components/question5/CounterMemo.js";
import HeavyCalculation from "./components/question5/HeavyCalculation.js";
import TodoList from "./components/question5/TodoList.js";
import LiveTime from "./components/question5/LiveTime.js";

function App() {
  const [num, setNum] = useState(5);
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");

  const addTodo = () => {
    if (task.trim() === "") return;
    setTodos((prevTodos) => [...prevTodos, task]);
    setTask("");
  };

  const removeTodo = useCallback((index) => {
    setTodos((prevTodos) => prevTodos.filter((_, i) => i !== index));
  }, []);

  const handleClick = () => alert("Button clicked!");
  const items = ["Apple", "Banana", "Cherry", "Date", "Elderberry"];

  return (
    <>
      <Router>
        <div className="app-container">
          {/* Question 1 */}
          <section>
            <h1 className="section-title">Question 1</h1>
            <WelcomeMessage />
            <DateDisplay />
            <HobbiesList />
            <Button text="Click Me" color="green" onClick={handleClick} />
            <ProfileCard name="Rukundo Wilson" age={21} email="rukundo@ewilson.com" />
            <ItemsList items={items} />
          </section>

          {/* Question 2 */}
          <section>
            <h1 className="section-title">Question 2</h1>
            <ToggleButton />
            <Counter />
            <HoverBackground />
            <Form />
            <DropdownMenu />
          </section>

          {/* Question 3 */}
          <section>
            <h1 className="section-title">Question 3</h1>
            <LoginForm />
            <ValidatedForm />
            <ControlledForm />
            <MultiStepForm />
            <CheckboxForm />
          </section>

          {/* Question 4 */}
          <section>
            <h1 className="section-title">Question 4</h1>
            <NavigationBar />
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/product/:id" element={<ProductDetails />} />
              <Route path="*" element={<NotFound />} />
            </Routes>
          </section>

          {/* Question 5 */}
          <section>
            <h1 className="section-title">Question 5</h1>
            <Parent />
            <CounterMem />
            <div>
              <input
                type="number"
                value={num}
                onChange={(e) => setNum(parseInt(e.target.value) || 0)}
                placeholder="Enter Number"
              />
              <HeavyCalculation num={num} />
            </div>
            <div>
              <input
                type="text"
                value={task}
                onChange={(e) => setTask(e.target.value)}
                placeholder="Enter a task..."
              />
              <button onClick={addTodo}>Add Task</button>
              <TodoList todos={todos} removeTodo={removeTodo} />
            </div>
            <LiveTime />
          </section>
        </div>
      </Router>
    </>
  );
}

export default App;
